'use strict'

/**
 * @module      sourceType
 * @desc        source 타입을 정의
 * @since       0.0.1
 * @author      예장해
 * @date        2019-01-23
 * @example
 * const SOURCE_TYPE = require('sourceType')
 * console.log(SOURCE_TYPE.DEFAULT)     // 0
 * console.log(SOURCE_TYPE.FILE)        // 1
*/

Object.defineProperties(
    exports,
    {
        /**
         * @since       0.0.1
         * @author      예장해
         * @date        2019-01-23
         * @public
         * @static
         * @constant    {Integer}   DEFAULT
         * @default     0
         * @desc        sourceType이 지정되지 않을 경우 내부적으로 사용
        */
        DEFAULT: {
            value: 0
        },
        /**
         * @since       0.0.1
         * @author      예장해
         * @date        2019-01-23
         * @public
         * @static
         * @constant    {Integer}   FILE
         * @default     1
         * @desc        sourceType을 FILE로 지정
        */
        FILE: {
            value: 1
        }
    })
