'use strict'

const signal = require('./signal')
/**
 * @module      default
 * @since       0.0.1
 * @desc        default모듈은 process 모듈의 기본 설정값을 정의한 읽기 전용 모듈로, process 모듈 내부에서 사용
 * @date        2019-12-30
 * @requires    {@link module:signal signal}
*/
Object.defineProperties(
    exports,
    {
        /**
         * @since       0.0.1
         * @date        2019-12-30
         * @constant
         * @static
         * @public
         * @desc        Process class의 config.retryCnt의 default값 정의
         * @type        {Integer}
         * @default     3
         * @see         {@link Process Process constructor}
        */
        RETRY_CNT: {
            enumerable: true,
            value: 3
        },
        /**
         * @since       0.0.1
         * @date        2019-12-30
         * @constant
         * @static
         * @public
         * @desc        Process class의 config.retryInterval의 default값 정의
         * @type        {Integer}
         * @default     1
         * @see         {@link Process Process constructor}
        */
        RETRY_INTERVAL: {
            enumerable: true,
            value: 1
        },
        /**
         * @since       0.0.1
         * @date        2019-12-30
         * @constant
         * @static
         * @public
         * @desc        child process 종료 signal 정의
         * @type        {String}
         * @default     signal.SIGKILL
         * @see         {@link module:signal signal}
        */
        STOP_SIGNAL: {
            enumerable: true,
            value: signal.SIGKILL
        },
        /**
         * @since       0.0.1
         * @date        2019-12-30
         * @constant
         * @static
         * @public
         * @desc        process 정상 종료 리턴 코드
         * @type        {Number}
         * @default     0
        */
        SUCCESS_CODE: {
            enumerable: true,
            value: 0
        }
    })
