'use strict'

/**
 * @module  config
 * @desc    sys-info 모듈의 설정 정보 관리 모듈
 * @public
 * @since   1.0.0
 * @date    2020-01-16
*/
Object.defineProperties(
    exports,
    {
        /**
         * @name        set:config
         * @memberof    module:config
         * @desc        모듈 설정 정보 세팅
         * @function
         * @static
         * @public
         * @param   {Object}    config                          - 모듈 설정
         * @param   {Boolean}   config.useGPU                   - GPU 사용 여부
         * @param   {Boolean}   config.isDisplayUnit            - 단위 표시 여부
         * @param   {Object}    config.gpu                      - gpu 관련 설정
         * @param   {Boolean}   config.gpu.persistanceMode      - nvidia gpu persistance mode 설정
         * @since   1.0.0
         * @date    2020-01-15
         * @todo    config.gpu.persistanceMode 초기 세팅이나 변경시, 자동으로 persistanceMode 세팅하게 기능 추가
        */
        config: {
            set: (config) => {
                config && Object.assign(exports, config)
            }
        },
        /**
         * @name    useGPU
         * @desc    GPU 사용 여부
         * @type    {Boolean}
         * @public
         * @static
         * @since   1.0.0
         * @date    2020-01-16
        */
        useGPU: {
            enumerable: true,
            writable: true,
            value: false
        },
        /**
         * @name    isDisplayUnit
         * @desc    단위 표시 여부
         * @type    {Boolean}
         * @public
         * @static
         * @since   1.0.0
         * @date    2020-01-16
        */
        isDisplayUnit: {
            enumerable: true,
            writable: true,
            value: false
        },
        /**
         * @name    gpu
         * @desc    GPU 설정
         * @type    {Object}
         * @public
         * @static
         * @since   1.0.0
         * @date    2020-01-16
        */
        gpu: {
            enumerable: true,
            writable: true,
            value: {}
        }
    })
