'use strict'


/**
 * @class    Job
 * @classdesc   
 * @since       1.0.0
 * @date        2020-01-22
*/


// * @property    {any=}      id              Job 구분자 (사용자 정의)
// * @property    {Number=}   [priority=0]    Job 우선순위 (number가 작을 수록 우선)
// * @property    {any=}      serviceType     Worker 비즈니스 구분자<br/>
// *                                          Job 실행시, 어떤 Worker에서 실행할지 구분하는 값
// * @property    {any=}      status          Job 상태 (사용자 정의)
class Job {
    constructor() {
        Object.defineProperties(
            this,
            {
                /**
                 * @name        _id
                 * @memberof    Job
                 * @desc        Job 구분자 (사용자 정의)
                 * @type        {Number|String|Symbol}
                 * @private
                 * @instance
                 * @since       1.0.0
                 * @date        2020-01-30
                */
                _id: {
                    writable: true,
                    value: undefined
                },
                /**
                 * @name        _serviceType
                 * @memberof    Job
                 * @desc        Worker 비즈니스 구분자<br/>
*                               Job 실행시, 어떤 Worker에서 실행할지 구분하는 값
                 * @type        {String}
                 * @private
                 * @instance
                 * @since       1.0.0
                 * @date        2020-01-30
                */
                _serviceType: {
                    writable: true,
                    value: undefined
                },
                /**
                 * @name        _priority
                 * @memberof    Jobs
                 * @desc        Job 우선순위 (number가 작을 수록 우선)
                 * @type        {Number}
                 * @private
                 * @instance
                 * @since       1.0.0
                 * @date        2020-01-30
                */
                _priority: {
                    writable: true,
                    value: undefined
                },
                /**
                 * @name        _status
                 * @memberof    Jobs
                 * @desc        Job 상태 (사용자 정의)
                 * @type        {any=}
                 * @private
                 * @instance
                 * @since       1.0.0
                 * @date        2020-01-30
                */
                _status: {
                    writable: true,
                    value: undefined
                }
            })
    }

    /**
     * @name        get:id
     * @memberof    Job
     * @desc        id 리턴
     * @function
     * @public
     * @instance
     * @returns     {Number|String|Symbol}    id
     * @since       1.0.0
     * @date        2020-01-30
    */
    get id() {
        return this._id
    }

    /**
     * @name        set:id
     * @memberof    Job
     * @desc        id 설정
     * @function
     * @public
     * @instance
     * @since       1.0.0
     * @date        2020-01-30
    */
    set id(id) {
        this._id = id
    }

    /**
     * @name        get:serviceType
     * @memberof    Job
     * @desc        serviceType 리턴
     * @function
     * @public
     * @instance
     * @returns     {String}    serviceType
     * @since       1.0.0
     * @date        2020-01-30
    */
    get serviceType() {
        return this._serviceType
    }

    /**
     * @name        set:serviceType
     * @memberof    Job
     * @desc        serviceType 설정
     * @function
     * @public
     * @instance
     * @since       1.0.0
     * @date        2020-01-30
    */
    set serviceType(serviceType) {
        this._serviceType = serviceType
    }

    /**
     * @name        get:priority
     * @memberof    Job
     * @desc        priority 리턴
     * @function
     * @public
     * @instance
     * @returns     {Number}    priority
     * @since       1.0.0
     * @date        2020-01-30
    */
    get priority() {
        return this._priority
    }

    /**
     * @name        set:priority
     * @memberof    Job
     * @desc        priority 설정
     * @function
     * @public
     * @instance
     * @since       1.0.0
     * @date        2020-01-30
    */
    set priority(priority) {
        this._priority = priority
    }
    
    /**
     * @name        get:status
     * @memberof    Job
     * @desc        status 리턴
     * @function
     * @public
     * @instance
     * @returns     {Number}    status
     * @since       1.0.1
     * @date        2020-01-30
    */
    get status() {
        return this._status
    }

    /**
     * @name        set:status
     * @memberof    Job
     * @desc        status 설정
     * @function
     * @public
     * @instance
     * @since       1.0.1
     * @date        2020-01-30
    */
    set status(status) {
        this._status = status
    }
}

module.exports = Job