'use strict'

/**
 * @class           error/WorkerLoadError
 * @extends         Error
 * @param           {Object}   config
 * @param           {String}   config.msg
 * @param           {String=}  config.serviceType
 * @param           {String=}  config.workerPath
 * @param           {Error=}   config.err
 */
class WorkerLoadError extends Error {
    constructor (config) {
        const {msg, serviceType, workerPath, err} = config
        super(msg)
        Object.defineProperties(
            this,
            {
                err: {
                    value: err
                },
                serviceType: {
                    value: serviceType
                },
                workerPath: {
                    value: workerPath
                }
            }
        )
    }
}
module.exports = WorkerLoadError;